<?php	wp_reset_postdata();	$number    		= isset( $instance['numberposts'] ) ? intval($instance['numberposts']) : 5;	if ( ! defined( 'ABSPATH' ) ) exit; // Exit if accessed directly		global $product, $woocommerce_loop,$wp_query;	$related = $product->get_related( $number );	if ( sizeof( $related ) == 0 ) return;	$args = apply_filters( 'woocommerce_related_products_args', array(		'post_type'            => 'product',		'ignore_sticky_posts'  => 1,		'no_found_rows'        => 1,		'posts_per_page'       => $number,		'post__in'             => $related,		'post__not_in'         => array( $product->id )	) );	$num_post  = count($related);	$relate = new WP_Query( $args );	if ($relate->have_posts()) :	$i = 0;	$j = 0;	$k = 0;?>			<div id="<?php echo $widget_id; ?>" class="carousel slide sw-related-product" data-ride="carousel">		<ul class="carousel-indicators">		<?php while( $relate->have_posts() ) : $relate->the_post(); 				if( $j % 3 == 0 ){			?>				<li data-target="#<?php echo $widget_id; ?>" data-slide-to="<?php echo $k; ?>" class="<?php if( $j == 0 ){echo 'active'; } ?>"></li>							<?php $k++; } $j++;  endwhile; wp_reset_query(); ?>		</ul>		<div class="carousel-inner">			<?php 			while ($relate->have_posts()) : $relate->the_post();			global $product, $post, $wpdb, $average;			$count = $wpdb->get_var("				SELECT COUNT(meta_value) FROM $wpdb->commentmeta				LEFT JOIN $wpdb->comments ON $wpdb->commentmeta.comment_id = $wpdb->comments.comment_ID				WHERE meta_key = 'rating'				AND comment_post_ID = $post->ID				AND comment_approved = '1'				AND meta_value > 0			");			$rating = $wpdb->get_var("				SELECT SUM(meta_value) FROM $wpdb->commentmeta				LEFT JOIN $wpdb->comments ON $wpdb->commentmeta.comment_id = $wpdb->comments.comment_ID				WHERE meta_key = 'rating'				AND comment_post_ID = $post->ID				AND comment_approved = '1'			");			if( $i % 3 == 0 ){ 			?>			<div class="item <?php if( $i == 0 ){ echo 'active'; } ?>">			<?php } ?>				<div class="item-detail">					<div class="item-img">						<a href="<?php the_permalink(); ?>"><?php if( has_post_thumbnail() ){  echo get_the_post_thumbnail( $relate->post->ID, 'shop_thumbnail' ); }else{ echo woocommerce_placeholder_img( 'shop_thumbnail' ); } ?></a>					</div>					<div class="item-content">						<h4><a href="<?php the_permalink(); ?>"><?php the_title(); ?></a></h4>						<?php							if( $count > 0 ){								$average = number_format($rating / $count, 1);						?>							<div class="star"><span style="width: <?php echo ($average*13).'px'; ?>"></span></div>													<?php } else { ?>													<div class="star"></div>													<?php } ?>						<p><?php echo $product->get_price_html(); ?></p>						<div class="review">							<span><?php echo $count; ?> <?php _e(' review(s)', 'yatheme'); ?></span>											</div>					</div>				</div>			<?php if( ( $i+1 )%3==0 || ( $i+1 ) == $num_post  ){?> </div><?php } ?>			<?php $i++; endwhile; ?>		</div>	</div>			<?php	endif;	wp_reset_query();?>