<?php	$default = array(		'category' => $category,		'orderby' => $orderby,		'numberposts' => $numberposts,		'length' => $length	);	$list = get_posts($default);	$i = 0;	if (!wp_script_is('slider_js')) {	wp_enqueue_script('slider_js');	}	if (!wp_script_is('hover_js')) {		wp_enqueue_script('hover_js');	}?><div class="sw-slider" <?php if( $background ){?> style="background: url(<?php echo $background; ?>);" <?php } ?>>	<div class="container slide-overflow">		<div class="slideshow" id="<?php echo $widget_id; ?>">			<ul id="slides-<?php echo $widget_id; ?>">				<?php foreach ( $list as $i => $post ) {?>				<li class="slide item <?php echo $animation; ?><?php if( $i == 0 ) echo " active"; ?>">					<?php echo get_the_post_thumbnail($post->ID,'slide-thumb');?>					<div class="bg-img">						<div class="bg-img-content">							<h4><?php echo $post->post_title;?></h4>							<div class="slider-category">								<span><?php _e('Category: ', 'yatheme'); ?></span>								<?php 																		$categories = get_the_category($post->ID);									foreach( $categories as $category ){ ?>										<a href="<?php echo get_category_link( $category -> term_id ); ?> "><?php echo $category -> name ; ?></a>								<?php }	?>							</div>							<p>								<?php									$text = strip_shortcodes( $post->post_content );									$text = apply_filters('the_content', $text);									$text = str_replace(']]>', ']]&gt;', $text);									$content = wp_trim_words($text, $length);									echo $content;								?>							</p>						</div>					</div>					<a href="<?php echo get_permalink( $post->ID ); ?>" class="readmore"><span><?php _e('Read More','yatheme');?></span><i class="icon-circle-arrow-right"></i></a>				</li>				<?php 					$i++;				}				?>			</ul>		</div>	</div>	<div class="slide-nav">		<a href="#" class="carousel-control prev" id="prev-<?php echo $widget_id; ?>"></a>		<a href="#" class="carousel-control next" id="next-<?php echo $widget_id; ?>"></a>	</div></div><?phpadd_action('wp_footer', array($this, 'add_script_slider'), 210);?>