<?php $interval     	= isset( $instance['interval'] )      ? intval($instance['interval']) : 4000;$hover_stop     = isset( $instance['hover_stop'] )      ? strip_tags($instance['hover_stop']) : 'false';$auto_play      = isset( $instance['auto_play'] )      ? strip_tags($instance['auto_play']) : 'true';$full_width      = isset( $instance['full_width'] )      ? strip_tags($instance['full_width']) : 'true';$number_thumb   = isset( $instance['number_thumb'] )      ? intval($instance['number_thumb']) : 4;if (!isset($instance['category'])){	$instance['category'] = 0;}extract($instance);$default = array(	'category' => $category,	'orderby' => $orderby,	'order' => $order,	'include' => $include,	'exclude' => $exclude,	'post_status' => 'publish',	'numberposts' => $numberposts);$list = get_posts($default);if ( !wp_style_is('cslider_css') && $full_width == 'true' ) {	wp_enqueue_style('cslider_css');}if (!wp_script_is('cslider_js')) {	wp_enqueue_script('cslider_js');}if ( count($list) > 0 ){?>	<div id="<?php echo $widget_id; ?>" class="da-slider slider-content<?php if( $full_width =='false' ){ echo ' slider-content-v1'; }?>" <?php if( $height || $background ){?>style="height: <?php echo $height; ?>px; background: url(<?php echo $background; ?>);" <?php } ?>>		<div class="<?php if( $full_width =='true' ){ echo 'container '; }?>container-slider">		<?php 		foreach ( $list as $i => $post ) { ?>			<div class="da-slide paral-slide<?php $i = ($i) % 5; echo ($i+1);?>">			<?php if( get_entry_content_asset( $post->ID ) != null ){?>					<div class="da-video"><?php echo get_entry_content_asset( $post->ID ); ?></div>				<?php }else{ ?>				<div class="da-slide-title">					<a href="<?php echo get_permalink($post); ?>" class="slider-title"><?php echo $post->post_title; ?></a>					<a href="<?php echo get_permalink($post); ?>" class="slider-readmore"><img src="<?php echo get_template_directory_uri();?>/assets/img/bt-slide-readmore.png" alt=""/></a>					<div class="slide-content">						<p>						<?php 							if ( preg_match('/<!--more(.*?)?-->/', $post->post_content, $matches) ) {								$content = explode($matches[0], $post->post_content, 2);								$content = $content[0];								echo $content;							} else {								echo self::ya_trim_words( $post->post_content, $length, ' ' );							}						?>						</p>											</div>					</div>									<div class="da-img"><?php if ( has_post_thumbnail($post->ID) ) { echo get_the_post_thumbnail( $post->ID , 'full');} ?></div>				<?php } ?>			</div>		<?php } ?>		</div>		<?php if( $thumbnail == 1 ){ ?>		<div class="da-nav">			<div class="da-nav-margin">				<ul class="da-dots">					<?php foreach ( $list as $i => $post ) { ?>						<li class="<?php if( $i == 0 ) { echo 'da-dots-current'; }?>"><?php echo get_the_post_thumbnail( $post->ID , 'full'); ?></li>					<?php } ?>				</ul>			</div>		</div>		<?php } ?>		<nav class="da-arrows">			<span class="da-arrows-prev"></span>			<span class="da-arrows-next"></span>		</nav>		<div class="slider-mask"></div>	</div>	<script type="text/javascript">		jQuery(document).ready(function($) {			jQuery(".slider-content").each(function(){				jQuery("#" + this.id).cslider({					bgincrement: 0,					autoplay	: <?php echo $auto_play; ?>,					hover_stop	: <?php echo $hover_stop; ?>,					interval	: <?php echo $interval; ?>,					visible		: <?php echo $number_thumb; ?>				});			});			jQuery(".slider-content").each(function(){				jQuery("#" + this.id).css("display", "block");			});	   });	</script><?php}?>