<?php 	$latest_posts    		= isset( $instance['latest_posts'] ) ? intval($instance['latest_posts']) : 5;$popular_posts    		= isset( $instance['popular_posts'] ) ? intval($instance['popular_posts']) : 5;$length    		= isset( $instance['length'] ) ? intval($instance['length']) : 20;	?><?php function tabs_latest_posts($posts, $length)	{		$the_query = new WP_Query('showposts='.$posts .'&orderby=post_date&order=desc');		//$recent_post_num = 1;		?>		<div class="row-fluid">		<?php		while ($the_query->have_posts()) : $the_query->the_post();		?>			<div class="tab-item">				<div class="row-fluid">					<div class="date">						<span><?php echo date('d',strtotime(get_post()->post_date))?></span>						<span><?php echo date('M',strtotime(get_post()->post_date))?></span>					</div>                    <div class="content-item-tab">						<a title="<?php the_title(); ?>" href="<?php the_permalink() ?>"><?php the_title(); ?></a>					<p>						<?php //var_dump(get_the_content().'---'.$length);							echo wp_trim_words(get_the_content(), $length, '&hellip; <a href="' . get_permalink() . '">' . __('Read more', 'yatheme') . '</a>');						?>											</p>                    </div>				</div>			</div>		<?php		endwhile; ?>		</div>		<?php	}function tabs_popular_posts( $posts, $length)	{		$popular = new WP_Query('orderby=comment_count&posts_per_page='.$posts);		//$popular_post_num = 1; ?>		<div class="row-fluid tab-post">		<?php while ($popular->have_posts()) : $popular->the_post();		?>			<div class="tab-item">				<div class="row-fluid">						<div class="comment">							<?php if ((int)get_post()->comment_count >1) {								echo get_post()->comment_count. ' ';							} else								echo get_post()->comment_count. ' ';							?>						</div>					<div class="content-item-tab">						<a title="<?php the_title(); ?>" href="<?php the_permalink() ?>"><?php the_title(); ?></a>					<p><?php echo wp_trim_words(get_the_content(), $length, '&hellip; <a href="' . get_permalink() . '">' . __('Read more', 'yatheme') . '</a>'); ?></p>                    </div>				</div>			</div>		<?php		endwhile;		?>		</div>		<?php	}function tabs_tags_posts( )	{?>		<div class="tags">			<?php wp_tag_cloud('smallest=8&largest=8');?>        </div>	<?php }	?><div class="tabbable">			<ul class=" nav nav-tabs row-fluid">				<li class="active"><a href="#tab1" data-toggle="tab">Recent</a></li>				<li class=""><a href="#tab2" data-toggle="tab">Comment</a></li>				<li class=""><a  href="#tab3" data-toggle="tab">Tags</a></li>			</ul>			<div class="clear"></div>			<div class=" tab-content">				<div id="tab1" class="tab-pane  active">					<?php tabs_latest_posts($latest_posts, $length); ?>				</div>				<div id="tab2" class="tab-pane ">					<?php tabs_popular_posts($popular_posts, $length); ?>				</div>				<div id="tab3" class="tab-pane ">					<?php tabs_tags_posts(); ?>				</div>				<div class="clear" style="display: none;"></div>			</div>			<div class="clear"></div>		</div>